class Solution(object):
    def findLongestChain(self, pairs):
        """
        :type pairs: List[List[int]]
        :rtype: int
        """
        if not pairs:
            return 0

        pairs.sort()
        print(pairs)

        ret = 1
        old = pairs[0]
        for pair in pairs[1:]:
            # print(old, ret)
            if pair[0] > old[1]:
                ret += 1
                old = pair
            else:
                if pair[1] < old[1]:
                    old = pair
        return ret





